package labexam2;

import java.util.Stack;

public class second {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		


		Stack<Integer> stacks = new Stack<>();
		stacks.push(10);
		stacks.push(20);
		stacks.push(30);
		stacks.push(888);
		System.out.println("top: " + stacks.peek());
		stacks.pop();
		System.out.println("second: " + stacks.peek());
		


	}

}




